import React, { useContext, useState } from "react";
import { Row, Card } from 'react-bootstrap';
import { auth, signInWithGoogle, generateUserDocument } from "../firebase";

const Register = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState(null);

    const onRegister = (event, email, password) => {
      event.preventDefault();

      setEmail("");
      setPassword("");
    };


    const onSubmit = async (event, email, password) => {
        console.log(email, password);
        event.preventDefault();
        try{
            const {user} = await auth.createUserWithEmailAndPassword(email, password);
            generateUserDocument(user, {displayName});
        }
        catch(error){
            setError('Error Signing up with email and password');
         }
    };

    const onChange = (event) => {
        const {name, value} = event.currentTarget;

        if(name === 'email') {
            setEmail(value);
        }
        if(name === 'password') {
            setPassword(value);
        }
    };

    return (
        <Container>
            <Row className="justify-content-md-center">
                <Col md="auto">
                    <Card>
                        <Card.Body>
                            <Form>
                                <Form.Group controlId="formBasicEmail">
                                    <Form.Label>Email address</Form.Label>
                                    <Form.Control type="email" placeholder="Enter email" name="email" onChange={(event) => onChange(event)}/>
                                    <Form.Text className="text-muted">{error}</Form.Text>
                                </Form.Group>
                                <Form.Group controlId="formBasicPassword">
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control type="password" placeholder="Password" name="password" onChange={(event) => onChange(event)}/>
                                    <Form.Text className="text-muted">{error}</Form.Text>
                                </Form.Group>
                                <Button variant="primary" type="submit" onClick={(event) => {onSubmit(event, email, password)}}>Submit</Button>
                            </Form>
                            <Form.Group controlId="formBasicCheckbox" style={{ paddingTop: '1rem' }}>
                                <Card.Link href="/">Login</Card.Link>
                            </Form.Group>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
  	);
};
export default Register;
