import React, {useState} from "react";
import { Link } from "@reach/router";

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);

    const login = (event, email, password) => {event.preventDefault();};

    const onChange = (event) => {
        const {name, value} = event.currentTarget;

        if(name === 'email') {
            setEmail(value);
        }
        if(name === 'password'){
            setPassword(value);
        }
    };

    return (
        <div className="mt-8">
            <h1 className="text-3xl mb-2 text-center font-bold">Sign In</h1>
            <div className="border border-blue-400 mx-auto w-11/12 md:w-2/4 rounded py-8 px-4 md:px-8">{error !== null && <div className = "py-4 bg-red-600 w-full text-white text-center mb-3">{error}</div>}
            <form className="">
                <label htmlFor="email" className="block">Email:</label>
                <input type="email" className="my-1 p-1 w-full" name="email" value = {email} placeholder="E.g: faruq123@gmail.com" id="email" onChange = {(event) => onChange(event)} />
                <label htmlFor="password" className="block">Password:</label>
                <input type="password" className="mt-1 mb-3 p-1 w-full" name="password" value = {password} placeholder="Your Password" id="password" onChange = {(event) => onChange(event)} />
                <button className="bg-green-400 hover:bg-green-500 w-full py-2 text-white" onClick = {(event) => {login(event, email, password)}}>Login</button>
        </form>
        <p className="text-center my-3">or</p>
        <button
          className="bg-red-500 hover:bg-red-600 w-full py-2 text-white">
          Sign in with Google
        </button>
        <p className="text-center my-3">
          Don't have an account?{" "}
          <Link to="signUp" className="text-blue-500 hover:text-blue-600">
            Sign up here
          </Link>{" "}
          <br />{" "}
          <Link to = "passwordReset" className="text-blue-500 hover:text-blue-600">
            Forgot Password?
          </Link>
        </p>
      </div>
    </div>
  );
};
export default SignIn;
